var database = context.getVariable("target.database");
if (database === "warehouse") {
  context.setVariable("pam.api.url", "https://northamerica-northeast1-mvnjri-prod.cloudfunctions.net/pam-request-grant-create");
} else if (database === "notify") {
     context.setVariable("pam.api.url", "https://northamerica-northeast1-c4hnrd-prod.cloudfunctions.net/pam-request-grant-create-notify");
} else if (database === "docs") {
     context.setVariable("pam.api.url", "https://northamerica-northeast1-c4hnrd-prod.cloudfunctions.net/pam-request-grant-create-docs");
} else if (database === "auth") {
     context.setVariable("pam.api.url", "https://northamerica-northeast1-gtksf3-prod.cloudfunctions.net/pam-request-grant-create-auth-db");
} else {
  throw new Error("Invalid database");
}
